export class NonInfectiousComponent {
    constructor(apiService, toast) {
        this.apiService = apiService;
        this.toast = toast;
        this.content = {
            name: "Animal Disease Incidence Report",
            errors: [],
            animals: [{
                    symptoms: []
                }],
            diseases: [],
            fixedColumnHeader: [],
            columnHeader: [],
            forms: {
                district: {},
                institute: {},
                incharge: {},
                diseases: []
            },
            districts: [],
            institutes: [],
            incharges: [],
            apiLink: '/forms'
        };
        this.formIncidenceIsDisabled = false;
        this.saveFormTitle = 'Save and Continue';
        this.loading = false;
        this.formType = "Incidence";
    }
    async ngOnInit() {
        await this.getAnimals();
        await this.getDiseases();
        await this.getCurrentMonthForm();
    }
    async getCurrentMonthForm() {
        let $this = this;
        this.loading = true;
        this.apiService.getData(`/forms`, null).subscribe((res) => {
            this.loading = false;
            this.content.forms = res;
            console.log("get form", this.content);
            $this.content.forms.formDiseaseValues.forEach(function (fd) {
                console.log(fd.animal.id, fd.symptom.id);
            });
        }, error => {
            this.loading = false;
        });
    }
    async getAnimals() {
        this.loading = true;
        this.apiService.getData(`/master/animals`, null)
            .subscribe((res) => {
            this.loading = false;
            if (!res) {
                this.toast.error("Could not connect to Server. Try again after sometime.", {
                    position: 'top-right',
                    autoClose: true
                });
            }
            else {
                if (res.length <= 0) {
                    this.content.errors.push("No animals found.");
                }
                this.content.animals = res;
                this.content.animals.forEach(function (animal) {
                    animal.symptoms = [];
                    animal.disease = {};
                });
            }
        }, error => {
            this.loading = false;
            this.content.errors.push("Could not load data at this time. Try again later.");
        });
    }
    async getDiseases() {
        this.loading = true;
        this.apiService.getData(`/diseases/byCategory/1`, null)
            .subscribe((res) => {
            this.loading = false;
            if (!res) {
                this.toast.error("Could not connect to Server. Try again after sometime.", {
                    position: 'top-right',
                    autoClose: true
                });
            }
            else {
                if (res.length <= 0) {
                    this.content.errors.push("No Diseases found!!");
                }
                res.forEach(function (disease) {
                    disease.symptoms.forEach(function (_sym) {
                        _sym.total = 0;
                        _sym.subSymptoms.forEach(function (_sub) {
                            _sub.total = 0;
                        });
                    });
                });
                this.content.diseases = res;
                this.getColumnHeaders();
            }
        }, error => {
            this.loading = false;
            this.content.errors.push("Could not load data at this time. Try again later.");
        });
    }
    getFixedHeaders() {
        let flag = 0;
