export class EntryFormComponent {
    constructor(apiService, toast, authService, datePipe, currentRoute) {
        this.apiService = apiService;
        this.toast = toast;
        this.authService = authService;
        this.datePipe = datePipe;
        this.currentRoute = currentRoute;
        this.content = {
            name: "Animal Disease Incidence Report",
            errors: [],
            animals: [{
                    symptoms: []
                }],
            diseases: [],
            fixedColumnHeader: [],
            columnHeader: [],
            forms: {
                district: {},
                institute: {},
                incharge: {},
                diseases: []
            },
            districts: [],
            institutes: [],
            incharges: [],
            apiLink: '/forms',
            fileLocked: false,
            formName: "NonInfectious",
            user: {}
        };
        this.saveFormTitle = 'Save and Continue';
        this.loading = false;
        this.formType = "Incidence";
        this.openTab = "incidence";
        this.content.user = this.authService.userValue;
    }
    ngOnInit() {
        this.currentRoute.params.subscribe((param) => {
            this.content.formName = param.formName.replace("-", "");
            if (param.formName != "infectious") {
                this.content.FormsType_Incidence = 3;
                this.content.FormsType_Mortality = 4;
            }
            else {
                this.content.FormsType_Incidence = 1;
                this.content.FormsType_Mortality = 2;
            }
            this.getBaseData();
            this.getAnimals();
            this.getDiseases();
            this.getIncidenceForm(false);
        });
    }
    approveEdit() {
        this.apiService.putData(`/forms/${this.content.forms.id}/toggleDiseaseFormLock`, this.content.forms.id, null)
            .subscribe((res) => {
            if (!res) {
                this.toast.error("Could not connect to Server. Try again after sometime.", {
                    position: 'top-right',
                    autoClose: true
                });
            }
            else {
                this.toast.success("Form is unlocked, user can re-edit the form again.", {
                    position: 'top-right',
                    autoClose: true
                });
                this.getIncidenceForm(false);
            }
        }, error => {
            this.loading = false;
            this.content.errors.push("Could not load data at this time. Try again later.");
        });
    }
    getIncidenceForm(isAfterSaveLoad) {
        let $this = this;
        this.loading = true;
        this.apiService.postData(`/forms/get`, {
            createdDate: new Date(this.content.forms.createdDate),
            formType: this.content.FormsType_Incidence,
            formName: this.content.formName
        }).subscribe((res) => {
            this.loading = false;
            if (res != null) {
                this.content.forms = res;
                let timer = 0;
                $this.content.forms.formDiseaseValues.forEach(function (fd) {
                    setTimeout(() => {
                        if ($(`#animal_${fd.symptom.id}_${fd.animal.id}`).length <= 0) {
                            timer++;
                        }
                        else {
                            $(`#animal_${fd.symptom.id}_${fd.animal.id}`).val(fd.value);
                            clearTimeout();
                        }
                    }, timer);
                });
                if (isAfterSaveLoad) {
                    if (this.content.forms.currentStep == "incidence") {
                        this.openTab = "mortality";
                    }
                    else if (this.content.forms.currentStep == "mortality") {
                        this.saveFormTitle = "Submit";
                        this.openTab = "remarks";
                    }
                    else if (this.content.forms.currentStep == "remarks" ||
                        this.content.forms.currentStep == "completed") {
                        this.openTab = "completed";
                    }
                }
                this.content.fileLocked = this.content.forms.isLocked;
                this.getSelectedValues();
            }
            else {
                this.content.forms.currentStep = null;
                this.content.fileLocked = false;
            }
        }, error => {
            this.loading = false;
        });
    }
    getSelectedValues() {
        var timer = 1;
        if (!this.content.forms.district) {
            setTimeout(() => {
                if (this.content.forms.district) {
                    clearTimeout();
                }
                else {
                    timer++;
                }
            }, timer);
        }
        this.content.institutes = [] = this.content.districts.filter((a) => a.id == this.content.forms.district.id)[0].institutes;
        this.content.incharges = [] = this.content.institutes.filter((a) => a.id == this.content.forms.institute.id)[0].incharges;
        this.content.forms.createdDate = this.datePipe.transform(this.content.forms.createdDate, "yyyy-MM");
    }
    async getMortalityForm() {
        let $this = this;
        this.loading = true;
        await this.apiService.postData(`/forms/get`, {
            createdDate: new Date(this.content.forms.createdDate),
            formType: this.content.FormsType_Mortality,
            formName: this.content.formName
        }).subscribe((res) => {
            this.loading = false;
            if (res != null) {
                this.content.forms = res;
                let timer = 0;
                $this.content.forms.formDiseaseValues.forEach(function (fd) {
                    setTimeout(() => {
                        if ($(`#animal_${fd.symptom.id}_${fd.animal.id}`).length <= 0) {
                            timer++;
                        }
                        else {
                            $(`#animal_${fd.symptom.id}_${fd.animal.id}`).val(fd.value);
                            clearTimeout();
                        }
                    }, timer);
                });
                this.getSelectedValues();
            }
        }, error => {
            this.loading = false;
        });
    }
    calcTotal(symptom) {
        var sum = 0;
        $(`.animal_${symptom.id}`).each(function () {
            // @ts-ignore
            sum += +$(this).val();
        });
        $(`.animal_${symptom.id}_total`).val(sum);
    }
    async getAnimals() {
        this.loading = true;
        await this.apiService.getData(`/master/animals`, null)
            .subscribe((res) => {
            this.loading = false;
            if (!res) {
                this.toast.error("Could not connect to Server. Try again after sometime.", {
                    position: 'top-right',
                    autoClose: true
                });
            }
            else {
                if (res.length <= 0) {
                    this.content.errors.push("No animals found.");
                }
                this.content.animals = [] = res;
                this.content.animals.forEach(function (animal) {
                    animal.symptoms = [];
                    animal.disease = {};
                });
            }
        }, error => {
            this.loading = false;
            this.content.errors.push("Could not load data at this time. Try again later.");
        });
    }
    async getDiseases() {
        this.loading = true;
        await this.apiService.getData(`/diseases/byCategory/${this.content.formName == 'infectious' ? 2 : 1}`, null)
            .subscribe((res) => {
            this.loading = false;
            if (!res) {
                this.toast.error("Could not connect to Server. Try again after sometime.", {
                    position: 'top-right',
                    autoClose: true
                });
            }
            else {
                if (res.length <= 0) {
                    this.content.errors.push("No Diseases found!!");
                }
                res.forEach(function (disease) {
                    disease.symptoms.forEach(function (_sym) {
                        _sym.total = 0;
                        _sym.subSymptoms.forEach(function (_sub) {
                            _sub.total = 0;
                        });
                    });
                });
                this.content.diseases = [] = res;
                this.getColumnHeaders();
            }
        }, error => {
            this.loading = false;
            this.content.errors.push("Could not load data at this time. Try again later.");
        });
    }
    getFixedHeaders() {
        let flag = 0;
        this.content.diseases.forEach((disease) => {
            disease.symptoms.forEach((symptom) => {
                symptom.subSymptoms.forEach((subSymptom) => {
                    if (subSymptom.name != null && flag == 0) {
                        this.content.fixedColumnHeader.push("Sub Symptoms");
                        flag = 1;
                    }
                });
            });
        });
    }
    getColumnHeaders() {
        this.content.fixedColumnHeader = [];
        this.content.columnHeader = [];
        if (this.content.diseases.length > 0) {
            if (this.content.fixedColumnHeader.filter((a) => a == "Diseases").length <= 0)
                this.content.fixedColumnHeader.push("Diseases");
            this.content.diseases.every((disease) => {
                if (disease.symptoms.length > 0) {
                    if (this.content.fixedColumnHeader.filter((a) => a == "Symptoms").length <= 0)
                        this.content.fixedColumnHeader.push("Symptoms");
                    return false;
                }
                return true;
            });
        }
        this.getFixedHeaders();
        this.content.animals.forEach((animal) => {
            if (this.content.fixedColumnHeader.filter((a) => a == animal.name).length <= 0)
                this.content.columnHeader.push(animal.name);
        });
        if (this.content.fixedColumnHeader.filter((a) => a == "Total").length <= 0)
            this.content.columnHeader.push("Total");
    }
    save() {
        let $this = this;
        this.loading = false;
        this.content.forms.animals = this.content.animals;
        var _obj = this.createPostObject($this);
        this.apiService.postData(this.content.apiLink, _obj).subscribe((res) => {
