export class EntryFormComponent {
    constructor(apiService, toast, datePipe) {
        this.apiService = apiService;
        this.toast = toast;
        this.datePipe = datePipe;
        this.content = {
            name: "Animal Disease Incidence Report",
            errors: [],
            animals: [{
                    symptoms: []
                }],
            diseases: [],
            fixedColumnHeader: [],
            columnHeader: [],
            forms: {
                district: {},
                institute: {},
                incharge: {},
                diseases: []
            },
            districts: [],
            institutes: [],
            incharges: [],
            apiLink: '/forms',
            fileLocked: false
        };
        this.saveFormTitle = 'Save and Continue';
        this.loading = false;
        this.formType = "Incidence";
        this.openTab = "incidence";
    }
    async ngOnInit() {
        this.getBaseData();
        await this.getAnimals();
        await this.getDiseases();
    }
