            this.loading = false;
            if (res != null) {
                this.content.forms = res;
                let timer = 0;
                $this.content.forms.formDiseaseValues.forEach(function (fd) {
                    setTimeout(() => {
                        if ($(`#animal_${fd.symptom.id}_${fd.animal.id}`).length <= 0) {
                            timer++;
                        }
                        else {
                            $(`#animal_${fd.symptom.id}_${fd.animal.id}`).val(fd.value);
                            clearTimeout();
                        }
                    }, timer);
                });
                if (isAfterSaveLoad) {
                    if (this.content.forms.currentStep == "incidence") {
                        this.openTab = "mortality";
                    }
                    else if (this.content.forms.currentStep == "mortality") {
                        this.saveFormTitle = "Submit";
                        this.openTab = "remarks";
                    }
                    else if (this.content.forms.currentStep == "remarks" ||
                        this.content.forms.currentStep == "completed") {
                        this.openTab = "completed";
                    }
                }
                if (this.content.forms.isLocked) {
                    this.content.fileLocked = true;
                }
                this.getSelectedValues();
            }
        }, error => {
            this.loading = false;
        });
    }
    getSelectedValues() {
        var timer = 1;
        if (!this.content.forms.district) {
            setTimeout(() => {
                if (this.content.forms.district) {
                    clearTimeout();
                }
                else {
                    timer++;
                }
            }, timer);
        }
        this.content.districts.push(this.content.forms.district);
        $('#selDistrict').val(this.content.forms.district.id);
        this.content.institutes = [] = this.content.districts.filter((a) => a.id == this.content.forms.district.id)[0].institutes;
        this.content.incharges = [] = this.content.institutes.filter((a) => a.id == this.content.forms.institute.id)[0].incharges;
        $('#selInstitute').val(this.content.forms.institute.id);
        $('#selIncharge').val(this.content.incharges[0].id);
        $('#createdDate').val(this.datePipe.transform(this.content.forms.createdDate, "MMM yyyy"));
    }
    getMortalityForm() {
        let $this = this;
        this.loading = true;
        this.apiService.getData(`/forms/4/noninfectious`, { formType: 4, formName: "incidence" }).subscribe((res) => {
            this.loading = false;
            if (res != null) {
                this.content.forms = res;
                let timer = 0;
                $this.content.forms.formDiseaseValues.forEach(function (fd) {
                    setTimeout(() => {
                        if ($(`#animal_${fd.symptom.id}_${fd.animal.id}`).length <= 0) {
                            timer++;
                        }
                        else {
                            $(`#animal_${fd.symptom.id}_${fd.animal.id}`).val(fd.value);
                            clearTimeout();
                        }
                    }, timer);
                });
                this.getSelectedValues();
            }
        }, error => {
            this.loading = false;
        });
    }
