        var sum = 0;
        $(`.animal_${symptom.id}`).each(function () {
            // @ts-ignore
            sum += +$(this).val();
        });
        $(`.animal_${symptom.id}_total`).val(sum);
    }
    async getAnimals() {
        this.loading = true;
        this.apiService.getData(`/master/animals`, null)
            .subscribe((res) => {
            this.loading = false;
            if (!res) {
                this.toast.error("Could not connect to Server. Try again after sometime.", {
                    position: 'top-right',
                    autoClose: true
                });
            }
            else {
                if (res.length <= 0) {
                    this.content.errors.push("No animals found.");
                }
                this.content.animals = res;
                this.content.animals.forEach(function (animal) {
                    animal.symptoms = [];
                    animal.disease = {};
                });
            }
        }, error => {
            this.loading = false;
            this.content.errors.push("Could not load data at this time. Try again later.");
        });
    }
    getDiseases() {
        this.loading = true;
        this.apiService.getData(`/diseases/byCategory/1`, null)
            .subscribe((res) => {
            this.loading = false;
            if (!res) {
                this.toast.error("Could not connect to Server. Try again after sometime.", {
                    position: 'top-right',
                    autoClose: true
                });
            }
            else {
                if (res.length <= 0) {
                    this.content.errors.push("No Diseases found!!");
                }
                res.forEach(function (disease) {
                    disease.symptoms.forEach(function (_sym) {
                        _sym.total = 0;
                        _sym.subSymptoms.forEach(function (_sub) {
                            _sub.total = 0;
                        });
                    });
                });
                this.content.diseases = res;
                this.getColumnHeaders();
            }
        }, error => {
            this.loading = false;
            this.content.errors.push("Could not load data at this time. Try again later.");
        });
    }
    getFixedHeaders() {
        let flag = 0;
        this.content.diseases.forEach((disease) => {
            disease.symptoms.forEach((symptom) => {
                symptom.subSymptoms.forEach((subSymptom) => {
                    if (subSymptom.name != null && flag == 0) {
                        this.content.fixedColumnHeader.push("Sub Symptoms");
                        flag = 1;
                    }
                });
            });
        });
    }
    getColumnHeaders() {
        if (this.content.diseases.length > 0) {
            this.content.fixedColumnHeader.push("Diseases");
            this.content.diseases.every((disease) => {
                if (disease.symptoms.length > 0) {
                    this.content.fixedColumnHeader.push("Symptoms");
                    return false;
                }
                return true;
            });
        }
        this.getFixedHeaders();
        this.content.animals.forEach((animal) => {
            this.content.columnHeader.push(animal.name);
        });
        this.content.columnHeader.push("Total");
    }
    save() {
        let $this = this;
        this.loading = false;
        this.content.forms.animals = this.content.animals;
        var _obj = this.createPostObject($this);
        this.apiService.postData(this.content.apiLink, _obj).subscribe((res) => {
            this.loading = false;
            if (res) {
                this.getIncidenceForm(true);
            }
        });
    }
    createPostObject($this) {
        var _obj = {
            formType: this.openTab == 'incidence' ? 3 : 4,
            createdDate: this.content.forms.createdDate,
            district: this.content.forms.district,
            institute: this.content.forms.institute,
            incharge: this.content.forms.incharge,
            animals: this.content.animals,
            currentStep: this.openTab,
            remarks: this.content.forms.remarks,
            name: "NonInfectious"
        };
        _obj.animals.forEach(function (_animal) {
            _animal.symptoms = [];
            $this.content.diseases.forEach(function (disease) {
                disease.symptoms.forEach(function (symptom) {
                    var _obj = {
                        symptom: symptom,
                        value: $(`#animal_${symptom.id}_${_animal.id}`).val()
                    };
                    _obj.symptom.disease = {};
                    $.extend(_obj.symptom.disease, disease);
                    delete _obj.symptom.disease.symptoms;
                    _animal.symptoms.push(_obj);
                });
            });
        });
        return _obj;
    }
    getBaseData() {
        this.apiService.getData('/master/districts', null)
            .subscribe((res) => {
            if (!res) {
                this.toast.error("Could not connect to Server. Try again after sometime.", {
                    position: 'top-right',
                    autoClose: true
                });
            }
            else {
                if (res.data.length <= 0) {
                    this.content.errorMessage = "No Institutes found. Click 'Add New' to add.";
                }
                this.content.districts = res.data;
                console.log(res.data);
            }
        }, error => {
            this.content.errorMessage = "Could not load data at this time. Try again later.";
        });
    }
    changeDistrict(event) {
        this.content.institutes = [] = this.content.districts.filter((a) => a.id == event.target.value)[0].institutes;
    }
    changeInstitute(event) {
        this.content.incharges = [] = this.content.institutes.filter((a) => a.id == event.target.value)[0].incharges;
    }
}
