        this.loading = false;
        this.formType = "Incidence";
        this.openTab = "incidence";
    }
    async ngOnInit() {
        this.getBaseData();
        await this.getAnimals();
        await this.getDiseases();
    }
    ngAfterViewInit() {
        this.getIncidenceForm(false);
    }
    ngOnInit() {
    }
    getBaseData() {
        this.apiService.getData('/master/districts', null)
            .subscribe((res) => {
            if (!res) {
                this.toast.error("Could not connect to Server. Try again after sometime.", {
                    position: 'top-right',
                    autoClose: true
                });
            }
            else {
                if (res.data.length <= 0) {
                    this.content.errorMessage = "No Institutes found. Click 'Add New' to add.";
                }
                this.content.districts = res.data;
                console.log(res.data);
            }
        }, error => {
            this.content.errorMessage = "Could not load data at this time. Try again later.";
        });
    }
    changeDistrict(event) {
        this.content.institutes = [] = this.content.districts.filter((a) => a.id == event.target.value)[0].institutes;
    }
    changeInstitute(event) {
        this.content.incharges = [] = this.content.institutes.filter((a) => a.id == event.target.value)[0].incharges;
    }
