export class LaboratoryExaminationComponent {
    constructor(apiService, toast, datePipe, authService) {
        this.apiService = apiService;
        this.toast = toast;
        this.datePipe = datePipe;
        this.authService = authService;
        this.content = {
            name: "Laboratory Examination Form",
            errors: [],
            categories: [],
            samples: [],
            forms: {
                district: {}
            },
            fixedColumnHeader: [],
            columnHeader: [],
            apiLink: '/forms/laboratory',
        };
        this.loading = false;
    }
    async ngOnInit() {
        await this.getDistricts();
        await this.getAnimalCategories();
        await this.getSamples();
    }
    async ngAfterViewInit() {
        await this.getForm();
    }
    async getDistricts() {
        this.loading = true;
        this.apiService.getData('/master/districts', null)
            .subscribe((res) => {
            this.loading = false;
            if (!res) {
                this.toast.error("Could not connect to Server. Try again after sometime.", {
                    position: 'top-right',
                    autoClose: true
                });
            }
            else {
                if (res.data.length <= 0) {
                    this.content.errorMessage = "No Districts found. Click 'Add New' to add.";
                }
                this.content.districts = res.data;
                console.log(res.data);
            }
        }, error => {
            this.loading = false;
            this.content.errorMessage = "Could not load data at this time. Try again later.";
        });
    }
    async getAnimalCategories() {
        this.loading = true;
        this.apiService.getData(`/master/animals/category`, null)
            .subscribe((res) => {
            this.loading = false;
            if (!res) {
                this.toast.error("Could not connect to Server. Try again after sometime.", {
                    position: 'top-right',
                    autoClose: true
                });
            }
            else {
                if (res.length <= 0) {
                    this.content.errors.push("No animal categories found.");
                }
                this.content.categories = res;
                this.content.categories.forEach(function (_cat) {
                    _cat.subTests = [];
                });
            }
        }, error => {
            this.loading = false;
            this.content.errors.push("Could not load data at this time. Try again later.");
        });
    }
    getForm() {
        this.loading = true;
        this.apiService.postData(`/forms/laboratory/get`, {
            createdDate: new Date(this.content.forms.createdDate),
            formType: this.content.FormsType_Incidence,
            formName: this.content.formName
        }).subscribe((res) => {
            console.log(res);
            this.loading = false;
            if (res != null) {
                this.content.forms.createdDate = this.datePipe.transform(res.createdDate, "yyyy-MM");
                this.content.forms.district.id = res.district.id;
                let timer = 0;
                res.labFormValues.forEach(function (fd) {
                    setTimeout(() => {
                        if ($(`#animal_${fd.testTypes.id}_${fd.animalCategory.id}_noOfCase`).length <= 0) {
                            timer++;
                        }
                        else {
                            $(`#animal_${fd.testTypes.id}_${fd.animalCategory.id}_total`).val(fd.totalValue);
                            $(`#animal_${fd.testTypes.id}_${fd.animalCategory.id}_noOfCase`).val(fd.noOfPositiveCases);
                            clearTimeout();
                        }
                    }, timer);
                });
                if (this.content.forms.isLocked) {
                    this.content.fileLocked = true;
                }
                this.getSelectedValues();
                if (res.isLocked) {
                    if (this.authService.userValue.userRoles.includes("Head Office")
                        || this.authService.userValue.userRoles.includes("admin")) {
                        this.content.fileLocked = false;
                    }
                    else {
                        this.content.fileLocked = true;
                    }
                }
            }
            else {
                this.content.forms.currentStep = null;
                this.content.fileLocked = false;
            }
        }, error => {
            this.loading = false;
        });
    }
    getSelectedValues() {
        var timer = 1;
        if (!this.content.forms.district) {
            setTimeout(() => {
                if (this.content.forms.district) {
                    clearTimeout();
                }
                else {
                    timer++;
                }
            }, timer);
        }
        this.content.forms.createdDate = this.datePipe.transform(this.content.forms.createdDate, "yyyy-MM");
    }
    async getSamples() {
        this.loading = true;
        this.apiService.getData(`/samples`, null)
            .subscribe((res) => {
            this.loading = false;
            if (!res) {
                this.toast.error("Could not connect to Server. Try again after sometime.", {
                    position: 'top-right',
                    autoClose: true
                });
            }
            else {
                if (res.length <= 0) {
                    this.content.errors.push("No Samples test cases found. Try again after sometime.");
                }
                res.forEach(function (samples) {
                    samples.testTypes.forEach(function (testCase) {
                        testCase.subTests.forEach(function (_sub) {
                            _sub.total = 0;
                            _sub.totalNoOfveCases = 0;
                        });
                    });
                });
                this.content.samples = [] = res;
                this.getColumnHeaders();
            }
        }, error => {
            this.loading = false;
            this.content.errors.push("Could not load data at this time. Try again later.");
        });
    }
    getFixedHeaders() {
        let flag = 0;
        this.content.samples.forEach((sample) => {
            sample.testTypes.forEach((testType1) => {
                testType1.subTests.forEach((testType2) => {
                    if (testType2.name != null && flag == 0) {
                        this.content.fixedColumnHeader.push("Test Type 2");
                        flag = 1;
                    }
                });
            });
        });
    }
    getColumnHeaders() {
        if (this.content.samples.length > 0) {
            this.content.fixedColumnHeader.push("Samples");
            this.content.samples.every((sample) => {
                if (sample.testTypes.length > 0) {
                    this.content.fixedColumnHeader.push("Test Type 1");
                    return false;
                }
                return true;
            });
        }
        this.getFixedHeaders();
        this.content.categories.forEach((category) => {
            this.content.columnHeader.push(category.name);
        });
        this.content.columnHeader.push("Total");
    }
    save() {
        this.loading = true;
        let $this = this;
        this.loading = false;
        this.content.forms.animals = this.content.animals;
        var _obj = this.createPostObject($this);
        console.log("save", _obj);
        this.apiService.postData(this.content.apiLink, _obj).subscribe((res) => {
            this.loading = false;
            if (res) {
                this.getForm();
            }
        }, error => {
            this.loading = false;
        });
    }
    createPostObject($this) {
        var _obj = {
            createdDate: this.content.forms.createdDate,
            district: this.content.forms.district,
            samples: this.content.samples,
            remarks: this.content.forms.remarks,
            formName: this.content.formName,
            categories: this.content.categories
        };
        _obj.categories.forEach(function (_cat) {
            _cat.tests = [];
            $this.content.samples.forEach(function (_sample) {
                _sample.testTypes.forEach(function (_testTypes) {
                    _testTypes.subTests.forEach(function (_subTest) {
                        var _obj = {
                            testType: _subTest,
                            totalValue: $(`#animal_${_subTest.id}_${_cat.id}_total`).val(),
                            noOfPositiveCases: $(`#animal_${_subTest.id}_${_cat.id}_noOfCase`).val()
                        };
                        _obj.testType.sample = {};
                        $.extend(_obj.testType.sample, _sample);
                        delete _obj.testType.sample.testTypes;
                        _cat.tests.push(_obj);
                    });
                });
            });
        });
        return _obj;
    }
}
